INSERT INTO `csym_courses_content` (`course_content_id`, `course_id`, `tab_name`, `tab_content`) VALUES
(1, 1, 'tab1', '&lt;div class=&#039;container-data&#039;&gt;&lt;li id=&#039;tabbed-1&#039;&gt; &lt;div&gt;&lt;div&gt; &lt;p&gt;Standard entry requirements apply to this Advertising and Digital Marketing Degree.&lt;/p&gt; &lt;p&gt;A typical offer is around:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;BCC at A Level or&lt;/li&gt; &lt;li&gt;DMM at BTEC or&lt;/li&gt; &lt;li&gt;Pass (C or above) at T level.&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;For information on how to apply to study with us, please see our &lt;a href=&#039;/undergraduate/how-to-apply/&#039;&gt;&lt;strong&gt;How to Apply page.&lt;/strong&gt;&lt;/a&gt;&lt;/p&gt; &lt;h5&gt;Integrated Foundation Year Entry Requirements&lt;/h5&gt; &lt;p&gt;The standard entry requirements for the Integrated Foundation Year are:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;DEE at A level or&lt;/li&gt; &lt;li&gt;MPP at BTEC or&lt;/li&gt; &lt;li&gt;Pass (D or E) at T Level.&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;For more information on how to make an application please visit our How to Apply pages for &lt;a href=&#039;https://www.northampton.ac.uk/undergraduate/how-to-apply/&#039;&gt;&lt;strong&gt;Home and EU&lt;/strong&gt;&lt;/a&gt;, and &lt;strong&gt;&lt;a href=&#039;https://www.northampton.ac.uk/international/how-to-apply/&#039;&gt;International students&lt;/a&gt;&lt;/strong&gt;.&lt;/p&gt; &lt;h5&gt;English Language Requirements&lt;/h5&gt; &lt;p&gt;All International and EU students applying for a course with us must meet the following minimum English language requirements:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;IELTS 6.0 (or equivalent) with a minimum of 5.5 in all bands&amp;nbsp;&lt;/strong&gt;for study at undergraduate level&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;For information regarding English language requirements at the University, please see our &lt;strong&gt;&lt;a href=&#039;/international/english-language-requirements/&#039;&gt;IELTS page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt;&lt;/div&gt; &lt;/div&gt; &lt;/li&gt;&lt;/div&gt;'),
(4, 2, 'tab1', '&lt;div&gt;&lt;ul&gt;&lt;li&gt;&lt;div&gt;&lt;p&gt;&lt;strong&gt;Level:&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt; &lt;p&gt;Postgraduate&lt;/p&gt; &lt;/div&gt; &lt;/li&gt; &lt;li&gt; &lt;div&gt; &lt;p&gt;&lt;strong&gt;Duration:&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt; &lt;p&gt;&lt;strong&gt;Full Time:&lt;/strong&gt; 1 Year&lt;br&gt;&lt;strong&gt;Full Time Placement:&lt;/strong&gt; 2 Years&lt;/p&gt; &lt;/div&gt; &lt;/li&gt; &lt;li&gt; &lt;div&gt; &lt;p&gt;&lt;strong&gt;Starting:&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt; &lt;p&gt;September&lt;br&gt;February&lt;/p&gt; &lt;/div&gt; &lt;/li&gt; &lt;li&gt; &lt;div&gt; &lt;p&gt;&lt;strong&gt;UK Fees 23/24:&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt; &lt;p&gt;&pound;8,010 &lt;/p&gt; &lt;/div&gt; &lt;/li&gt; &lt;li&gt; &lt;div&gt; &lt;p&gt;&lt;strong&gt;International Fees 23/24:&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt; &lt;p&gt;&pound;16,500&lt;/p&gt; &lt;/div&gt; &lt;/li&gt; &lt;li&gt; &lt;div&gt; &lt;p&gt;&lt;strong&gt;Location:&lt;/strong&gt;&lt;/p&gt; &lt;/div&gt; &lt;div&gt; &lt;a title=&#039;View Location&#039; href=&#039;https://www.northampton.ac.uk/about-us/contact-us/find-us/&#039;&gt;Waterside&lt;/a&gt; &lt;/div&gt; &lt;/li&gt; &lt;/ul&gt; &lt;/div&gt;'),
(7, 3, 'tab1', '&lt;div&gt;&lt;p&gt;Standard entry requirements apply. A typical offer is:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;BCC at A Level or&lt;/li&gt; &lt;li&gt;DMM at BTEC or&lt;/li&gt; &lt;li&gt;Pass (C and above) at T Level.&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;For more information on how to make an application, please visit our &lt;a href=&#039;/undergraduate/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;&lt;strong&gt;How to Apply page&lt;/strong&gt;&lt;/a&gt;.&lt;/p&gt; &lt;p&gt;If you are an International student and would like information on making an application, please see our&amp;nbsp;&lt;strong&gt;&lt;a href=&#039;/international/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;How to Apply page&lt;/a&gt;&lt;/strong&gt;.&lt;/p&gt; &lt;h5&gt;Integrated Foundation Year Entry Requirements&lt;/h5&gt; &lt;p&gt;The standard entry requirements for the Integrated Foundation Year are:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;DEE at A level or&lt;/li&gt; &lt;li&gt;MPP at BTEC or&lt;/li&gt; &lt;li&gt;Pass (D or E) at T Level.&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;For more information on how to make an application please visit our How to Apply pages for &lt;a href=&#039;https://www.northampton.ac.uk/undergraduate/how-to-apply/&#039;&gt;&lt;strong&gt;Home and EU&lt;/strong&gt;&lt;/a&gt;, and &lt;a href=&#039;https://www.northampton.ac.uk/international/how-to-apply/&#039;&gt;&lt;strong&gt;International students&lt;/strong&gt;&lt;/a&gt;.&lt;/p&gt; &lt;h5&gt;English Language Requirements&lt;/h5&gt; &lt;p&gt;All International and EU students applying for a course with us must meet the following minimum English language requirements:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;IELTS 6.0 (or equivalent) with a minimum of 5.5 in all bands&amp;nbsp;&lt;/strong&gt;for study at undergraduate level.&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;For information regarding English language requirements at the University, please see our&amp;nbsp;&lt;strong&gt;&lt;a href=&#039;/international/english-language-requirements/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;IELTS&amp;nbsp;page&lt;/a&gt;&lt;/strong&gt;.&lt;/p&gt; &lt;/div&gt;'),
(10, 4, 'tab1', '&lt;div&gt;&lt;div&gt;&lt;p&gt;You will need to hold a First or Second class honours degree (or equivalent) in order to be eligible for this course. No work experience is required for admission onto this course.&lt;/p&gt;&lt;p&gt;For more information on how to make an application, please visit our &lt;strong&gt;&lt;a href=&#039;/postgraduate/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;How to Apply page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;If you are an International student and would like information on making an application, please see our&amp;nbsp;&lt;strong&gt;&lt;a href=&#039;/international/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;How to Apply page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h5&gt;English Language Requirements&lt;/h5&gt;&lt;p&gt;If English is not your first language, you must meet the following English language requirements:&lt;/p&gt;&lt;p&gt;IELTS 6.5 overall&amp;nbsp;with&amp;nbsp;a minimum of 6.0 in writing&amp;nbsp;and&amp;nbsp;5.5 in all other skills.&lt;/p&gt;&lt;p&gt;Equivalent qualifications are considered, for further information regarding English language requirements at the University, please see our &lt;strong&gt;&lt;a href=&#039;https://www.northampton.ac.uk/international/english-language-requirements/&#039;&gt;English Language Requirements&lt;/a&gt;&lt;/strong&gt;&amp;nbsp;page.&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;/div&gt;&lt;/div&gt;'),
(13, 5, 'tab1', '&lt;div&gt;&lt;p&gt;This course explores the exciting and dynamic world of digital marketing. You will investigate the debates around the Virtual Reality, the Metaverse, Artificial Intelligence, Digital Transformation and Digital skills that are framing the world around us.&lt;/p&gt; &lt;p&gt;Accredited by the Digital Marketing Institute (DMI), this Masters will also allow you to be certified as a Certified Digital Marketing Professional. Membership of the DMI is Included in the course fee and gives you access to a wealth of study materials and rich content via the DMI student online portal.&lt;/p&gt; &lt;h5&gt;Modules:&lt;/h5&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;Strategic Digital Marketing&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;This module addresses topic around &amp;nbsp;Digital Marketing Strategy, Search Engine Optimisation (SEO) &amp;amp; website optimisation. Investigate the world of YouTube and Display Advertising, evaluate effectiveness using Analytics with Google Analytics.&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;Social Media &amp;amp; Content Creation&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;Learn about the growth of social channels and gain an overview into the background &amp;amp; history of how social content and networks have developed. Explore issues around Disintermediation &amp;amp; reintermediation. Take part in debates and discussions around Ethics, the Creator Economy and how you can create your own effective content and be a Content Creator within the digital landscape as it is today.&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;Digital Diversity&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;Investigate bridging the digital divide and how the digital environment can be designed for inclusion for all users. Learn about Regulation &amp;amp; codes of conduct and issues around Digital discrimination. How can marketers and managers plan for a more inclusive digital workforce.&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;The Professional Digital Marketer&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;This module is designed to prepare you to be a digital marketing professional for the modern workplace. You will investigate the importance of Career goal setting, examine your Digital footprint audit, take part in Web portfolio designing and experience the importance of Network optimisation.&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;E-Commerce&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;Gain an overview of the e-commerce industry and trends. Understand digital consumer segments and the Online buying journey consumers take. Examine marketing models and concepts and the role of personalisation within e-commerce. Learn the practical realities and importance of analysing and forecasting online performance.&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;Digital Futures&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;What will the Digital Future bring or look like. Learn about Hype cycles, Futurology &ndash; and the study of research &amp;amp; development, Trend research, conduct Vertical dives &ndash; e.g. into healthcare martech or explore a Topical dive into a digital feature that is a discussion point of the moment&ndash; e.g. AI, blockchain, wearables, and perhaps for your future &ndash; Meet the PhD&hellip;.&lt;/p&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;Digital Project or Dissertation&lt;/strong&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;You will have the option to undertake a dissertation &ndash; a piece of individual, original research OR choose to take a practitioner-focused digital project.&lt;/p&gt; &lt;/div&gt;'),
(16, 6, 'tab1', '&lt;div&gt;&lt;p&gt;You will need to hold a First or Second class honours degree (or equivalent) in order to be eligible for this course. No work experience is required for admission onto this course.&lt;/p&gt;&lt;p&gt;For more information on how to make an application, please visit our &lt;strong&gt;&lt;a href=&#039;/postgraduate/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;How to Apply page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;If you are an International student and would like information on making an application, please see our &lt;strong&gt;&lt;a href=&#039;/international/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;How to Apply page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h5&gt;English Language Requirements&lt;/h5&gt;&lt;p&gt;If English is not your first language, you must meet the following English language requirements:&lt;/p&gt;&lt;p&gt;IELTS 6.5 overall with a minimum of 6.0 in writing and 5.5 in all other skills.&lt;/p&gt;&lt;p&gt;Equivalent qualifications are considered, for further information regarding English language requirements at the University, please see our &lt;strong&gt;&lt;a href=&#039;https://www.northampton.ac.uk/international/english-language-requirements/&#039;&gt;English Language Requirements&lt;/a&gt;&lt;/strong&gt; page.&lt;/p&gt;&lt;/div&gt;'),
(17, 6, 'tab2', '&lt;div&gt;&lt;h5&gt;23/24 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 23/24 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;Home:&lt;/strong&gt; &pound;8,010&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International: &lt;/strong&gt;&pound;16,500&lt;/li&gt;&lt;li&gt;&lt;strong&gt;Year 2 (Placement Route only):&lt;/strong&gt; &pound;1000* (for placements starting in September 2024 or January 2025)&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;*The placement fee amount will only be charged to you after you secure a placement and are enrolled on the work based project in year 2 of your course. You will not be charged this fee if you do not secure a work placement. Please note that the maximum period of study allowed for part-time courses is two years.&lt;/p&gt;&lt;p&gt;For information on the scholarships available to you, please see our &lt;strong&gt;&lt;a href=&#039;https://www.northampton.ac.uk/student-life/bursaries-scholarships-and-awards/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;scholarships page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h5&gt;Additional costs&lt;/h5&gt;&lt;p&gt;There are no additional costs for this course for students beginning their studies in September 2023. Should this change, applicants and students will be contacted by the university with details of the costs.&lt;/p&gt;&lt;h5&gt;22/23 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 22/23 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;Home:&lt;/strong&gt; &pound;7,650&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International: &lt;/strong&gt;&pound;15,000&lt;/li&gt;&lt;li&gt;&lt;strong&gt;Year 2 (Placement Route only):&lt;/strong&gt; &pound;500* (for placements starting in September 2023 or January 2024)&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;'),
(18, 6, 'tab3', '&lt;div&gt;&lt;p&gt;Upon completion of the Digital Marketing Strategy module, you will also become a &lt;a href=&#039;https://digitalmarketinginstitute.com/&#039;&gt;&lt;strong&gt;DMI Power Member&lt;/strong&gt;&lt;/a&gt; giving you access to over 1200 pieces of unique content including videos, tools, webinars and templates created and curated by some of the worlds leading subject matter experts. If you study with us, you have the added benefit of knowing that your marketing strategy course has been approved by an internationally recognised global certification provider. DMI certification is recognised by the worlds leading employers and will help you get the job you want.&lt;/p&gt;&lt;p&gt;&lt;img decoding=&#039;async&#039; src=&#039;https://www.northampton.ac.uk/wp-content/uploads/2016/01/dmi-professional-body-logo.png&#039;&gt;&lt;/p&gt;&lt;/div&gt;'),
(22, 8, 'tab1', '&lt;div&gt;&lt;div&gt;&lt;p&gt;Standard entry requirements apply for this Business Management degree. A typical offer would be:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;BCC at A Level or&lt;/li&gt;&lt;li&gt;DMM at BTEC or&lt;/li&gt;&lt;li&gt;Pass (C and above) at T Level.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;We welcome international applications and applications from students with non-traditional educational or professional qualifications.&lt;/p&gt;&lt;h5&gt;Integrated Foundation Year Entry Requirements&lt;/h5&gt;&lt;p&gt;The standard entry requirements for the Integrated Foundation Year are:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;DEE at A level or&lt;/li&gt;&lt;li&gt;MPP at BTEC or&lt;/li&gt;&lt;li&gt;Pass (D or E) at T level.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;For more information on how to make an application please visit our How to Apply pages for &lt;a href=&#039;https://www.northampton.ac.uk/undergraduate/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;Home and EU&lt;/a&gt;, and &lt;a href=&#039;https://www.northampton.ac.uk/international/how-to-apply/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;International students&lt;/a&gt;.&lt;/p&gt;&lt;h5&gt;English Language Requirements&lt;/h5&gt;&lt;p&gt;All International and EU students applying for a course with us must meet the following minimum English language requirements:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;IELTS 6.0 (or equivalent) with a minimum of 5.5 in all bands&amp;nbsp;&lt;/strong&gt;for study at undergraduate level&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;For information regarding English language requirements at the University, please see our &lt;a href=&#039;/international/english-language-requirements/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;IELTS page.&lt;/a&gt;&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;/div&gt;&lt;/div&gt;'),
(25, 9, 'tab1', '&lt;div&gt;&lt;div&gt;&lt;p&gt;This course for business analytics will be useful if you are a professional employed in business and/or technology sectors and wish to progress in your job by gaining a more specialist qualification.&lt;/p&gt;&lt;p&gt;If you hold a recognised first-or second-class honours degree (or equivalent) in Business, Management, Information Systems, or Information Technology-related courses you will be eligible to apply for this business analytics course. Graduates of the following Engineering disciplines are particularly suited to this programme and would be considered:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Aerospace&lt;/li&gt;&lt;li&gt;Civil&lt;/li&gt;&lt;li&gt;Electrical/Electronic&lt;/li&gt;&lt;li&gt;Mechanical&lt;/li&gt;&lt;li&gt;Robotics&lt;/li&gt;&lt;li&gt;General Engineering&lt;/li&gt;&lt;li&gt;Manufacturing&lt;/li&gt;&lt;li&gt;Industrial Engineering&lt;/li&gt;&lt;li&gt;Structural Engineering&lt;/li&gt;&lt;li&gt;Agricultural/Environmental&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;Biomedical, Chemical and other Engineering disciplines may be considered after being referred to the tutor.&lt;/p&gt;&lt;p&gt;The master in business analytics may also be very attractive to many international applicants.&lt;/p&gt;&lt;h5&gt;English Language Requirements&lt;/h5&gt;&lt;p&gt;All International and EU students applying for a course with us must meet the following minimum English language requirements:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;Minimum standard &ndash; IELTS 6.5 (or equivalent)&lt;/strong&gt;&lt;br&gt;for study at postgraduate level.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;For information regarding English language requirements at the University, please see our&amp;nbsp;&lt;a href=&#039;/international/english-language-requirements/&#039;&gt;IELTS&amp;nbsp;page.&lt;/a&gt;&lt;/p&gt;&lt;p&gt;If you would like information on making an application, please see our&amp;nbsp;&lt;a href=&#039;/international/how-to-apply/&#039;&gt;How to Apply page.&lt;/a&gt;&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;/div&gt;&lt;/div&gt;'),
(26, 9, 'tab2', '&lt;div&gt;&lt;div&gt;&lt;p&gt;Fees quoted for this business analytics course relate to study in the Academic Year 23/24 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;UK&lt;/strong&gt;: &pound;8,010&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International:&lt;/strong&gt; &pound;16,500&lt;/li&gt;&lt;/ul&gt;&lt;h5&gt;&lt;strong&gt;Additional costs&lt;/strong&gt;&lt;/h5&gt;&lt;p&gt;There are no additional costs for this master in business analytics for students beginning their studies in September 2023. Should this change, applicants and students will be contacted by the university with details of the costs.&lt;/p&gt;&lt;h5&gt;22/23 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 22/23 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;UK&lt;/strong&gt;: &pound;7,650&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International:&lt;/strong&gt; &pound;15,000&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;&lt;div&gt;&lt;/div&gt;&lt;/div&gt;'),
(27, 9, 'tab3', '&lt;div&gt;&lt;div&gt;&lt;h5&gt;23/24 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 23/24 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;UK Full Time&lt;/strong&gt;: &pound;9,250&lt;/li&gt;&lt;li&gt;&lt;strong&gt;UK Part Time:&lt;/strong&gt; &pound;1,540 (per 20 credit module)&lt;/li&gt;&lt;li&gt;&lt;strong&gt;UK Integrated Foundation Year:&lt;/strong&gt; &pound;9,250 for the foundation year; thereafter standard fees apply&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International Full Time&lt;/strong&gt;: &pound;14,750&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International Integrated Foundation Year&lt;/strong&gt;: &pound;14,750 for the foundation year; thereafter standard fees apply&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;For information on the scholarships available to you, please see our &lt;a href=&#039;https://www.northampton.ac.uk/student-life/bursaries-scholarships-and-awards/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;scholarships page.&lt;/a&gt;&lt;/p&gt;&lt;h5&gt;Additional costs&lt;/h5&gt;&lt;p&gt;There are no additional costs for our business management course for students beginning their studies in September 2023. Should this change, applicants and students will be contacted by the university with details of the costs.&lt;/p&gt;&lt;h5&gt;22/23 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 22/23 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;UK Full Time&lt;/strong&gt;: &pound;9,250&lt;/li&gt;&lt;li&gt;&lt;strong&gt;UK Part Time:&lt;/strong&gt; &pound;1,500 (per 20 credit module)&lt;/li&gt;&lt;li&gt;&lt;strong&gt;UK Integrated Foundation Year:&lt;/strong&gt; &pound;9,250 for the foundation year; thereafter standard fees apply&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International Full Time&lt;/strong&gt;: &pound;14,000&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International Integrated Foundation Year&lt;/strong&gt;: &pound;14,000 for the foundation year; thereafter standard fees apply&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;&lt;div&gt;&lt;/div&gt;&lt;/div&gt;'),
(28, 10, 'tab1', '&lt;div&gt;&lt;p&gt;Applications for the commerce and international business top up BA are welcomed from students with a HND (Edexcel, ABE, NCC) or Foundation Degree in Business (or overseas equivalent).&lt;/p&gt;&lt;h5&gt;English Language Requirements&lt;/h5&gt;&lt;p&gt;All International and EU students applying for this course with us must meet the following minimum English language requirements:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;Minimum standard - IELTS 5.5 (or equivalent) with a minimum of 5.5 in all bands&lt;/strong&gt;.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;For information regarding English language requirements at the University, please see our IELTS page.&lt;/p&gt;&lt;/div&gt;'),
(29, 10, 'tab2', '&lt;div&gt;&lt;h5&gt;23/24 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 23/24 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;UK Full Time:&lt;/strong&gt; &pound;9,250&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International Full Time:&lt;/strong&gt; &pound;15,500&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;For information on the scholarships available to you, please see our &lt;strong&gt;&lt;a href=&#039;https://www.northampton.ac.uk/student-life/bursaries-scholarships-and-awards/&#039; target=&#039;_blank&#039; rel=&#039;noopener&#039;&gt;scholarships page.&lt;/a&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h5&gt;Additional costs&lt;/h5&gt;&lt;p&gt;There are no additional costs for this course for students beginning their studies in September 2023. Should this change, applicants and students will be contacted by the university with details of the costs.&lt;/p&gt;&lt;h5&gt;22/23 Tuition Fees&lt;/h5&gt;&lt;p&gt;Fees quoted relate to study in the Academic Year 22/23 only and may be subject to inflationary increases in future years.&lt;/p&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;UK Full Time:&lt;/strong&gt; &pound;9,250&lt;/li&gt;&lt;li&gt;&lt;strong&gt;International Full Time:&lt;/strong&gt; &pound;14,500&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;&lt;div&gt;&lt;/div&gt;');

